import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const KakaoRedirect: React.FC = () => {
  const [status, setStatus] = useState('로그인 중입니다.');
  const backendUrl = process.env.REACT_APP_BACKEND_URL;
  const navigate = useNavigate();

  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const code = queryParams.get('code'); // 인가코드 추출

  useEffect(() => {
    const sendCodeToBackend = async () => {
      try {
        if (code && backendUrl) {
          // JWT 토큰을 요청할 때 POST 요청으로 변경
          const response = await axios.post(`${backendUrl}/api/auth/kakao`, { code });
          console.log('Response status:', response.status);
          
          // 예시: 서버 응답으로 사용자 정보와 JWT 토큰을 받는다고 가정
          const { token, user } = response.data;
          localStorage.setItem('jwtToken', token);
          localStorage.setItem('user', JSON.stringify(user)); // 사용자 정보를 로컬 스토리지에 저장

          setStatus('로그인 성공');
          console.log('Stored jwtToken:', localStorage.getItem('jwtToken'));
          console.log('Stored user:', localStorage.getItem('user'));

          // 로그인 성공 후 메인 페이지로 이동
          navigate('/main');
        } else {
          console.error('Authorization code or backend URL not found.');
          setStatus('로그인 실패: 인가 코드 또는 백엔드 URL을 찾을 수 없습니다.');
        }
      } catch (error) {
        if (axios.isAxiosError(error)) {
          console.error('Error details:', error.response?.data);
        } else {
          console.error('Unexpected error:', error);
        }
        setStatus('로그인 실패: 서버와의 통신 오류');
      }
    };
    sendCodeToBackend();
  }, [code, backendUrl, navigate]);

  return (
    <div>
      <p>{status}</p>
      <div className="spinner"></div>
    </div>
  );
};

export default KakaoRedirect;
