import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const KakaoRedirect: React.FC = () => {
  const [status, setStatus] = useState('로그인 중입니다.');
  const backendUrl = process.env.REACT_APP_BACKEND_URL;

  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const code = queryParams.get('code'); // 인가코드 추출

  useEffect(() => {
    const sendCodeToBackend = async () => {
      try {
        if (code && backendUrl) {
          // JWT 토큰을 요청할 때
          const response = await axios.get(`${backendUrl}/api/auth/kakao`, { params: { code } });
          console.log('Server response (JWT):', response.data);
          setStatus('로그인 성공');
          
          // 또는 사용자 정보를 요청할 때
          // const response = await axios.get(`${backendUrl}/api/auth/kakao/info`, { params: { code } });
          // console.log('Server response (User Info):', response.data);
          // setStatus('로그인 성공');
        } else {
          console.error('Authorization code or backend URL not found.');
          setStatus('로그인 실패: 인가 코드 또는 백엔드 URL을 찾을 수 없습니다.');
        }
      } catch (error) {
        console.error('Error sending code to backend:', error);
        setStatus('로그인 실패: 서버와의 통신 오류');
      }
    };
    sendCodeToBackend();
  }, [code, backendUrl]);

  return (
    <div>
      <p>{status}</p>
      <div className="spinner"></div>
    </div>
  );
};

export default KakaoRedirect;
