import React, { useEffect, useState } from 'react';
import axios from 'axios';

interface UserInfo {
  nickname: string;
  email: string;
  createdAt: string;
}

const UserInfoComponent: React.FC = () => {
  const [userInfo, setUserInfo] = useState<UserInfo | null>(null);
  const [status, setStatus] = useState('사용자 정보를 불러오는 중입니다.');
  const backendUrl = process.env.REACT_APP_BACKEND_URL;

  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        const token = localStorage.getItem('jwtToken');
        if (token && backendUrl) {
          const response = await axios.get<UserInfo>(`${backendUrl}/api/user/info`, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });
          setUserInfo(response.data);
          setStatus('사용자 정보 불러오기 성공');
        } else {
          console.error('JWT 토큰 또는 백엔드 URL을 찾을 수 없습니다.');
          setStatus('사용자 정보 불러오기 실패: JWT 토큰 또는 백엔드 URL을 찾을 수 없습니다.');
        }
      } catch (error) {
        if (axios.isAxiosError(error)) {
          console.error('Error details:', error.response?.data);
        } else {
          console.error('Unexpected error:', error);
        }
        setStatus('사용자 정보 불러오기 실패: 서버와의 통신 오류');
      }
    };
    fetchUserInfo();
  }, [backendUrl]);

  if (!userInfo) {
    return <p>{status}</p>;
  }

  return (
    <div>
      <h1>{userInfo.nickname}님, 환영합니다!</h1>
      <p>Email: {userInfo.email}</p>
      <p>가입일: {new Date(userInfo.createdAt).toLocaleDateString()}</p>
    </div>
  );
};

export default UserInfoComponent;
