import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserInfo: React.FC = () => {
  const [user,setUser] = useState<any>(null); // 사용자 정보 상태
  const [error, setError] = useState<string | null>(null); // 오류 상태
  const [loading, setLoading] = useState<boolean>(true); // 로딩 상태
  const backendUrl = process.env.REACT_APP_BACKEND_URL;

  useEffect(() => {
    const fetchUserInfo = async () => { // async로 선언
      try {
        // 사용자 정보 요청
        const accesscode = localStorage.getItem('code');
        console.log("카카오리다이렉트에서 저장된 인가코드"+ accesscode);
        if (accesscode) {
          const userInfoResponse = await axios.get(`${backendUrl}/api/auth/kakao/info`, {
            headers: {
              'Authorization': `Bearer ${accesscode}` // Authorization 헤더에 JWT 토큰 추가
            }
          });
          setUser(userInfoResponse.data);
          localStorage.setItem('user', JSON.stringify(userInfoResponse.data));
          console.log('Stored user info:', localStorage.getItem('user'));
        }
      } catch (err: unknown) {
        if (err instanceof Error) {
          setError(err.message); // 오류 메시지 설정
        } else {
          setError('알 수 없는 오류 발생'); // 알 수 없는 오류 처리
        }
      } finally {
        setLoading(false); // 로딩 완료
      }
    };

    fetchUserInfo();
  }, [backendUrl]); // 의존성 배열에 backendUrl 추가

  if (loading) return <div>로딩 중...</div>;
  if (error) return <div>오류 발생: {error}</div>;
  if (!user) return <div>사용자 정보를 찾을 수 없습니다.</div>;

  return (
    <div>
      <h1>사용자 정보</h1>
      <p>이름: {user.name}</p>
      <p>이메일: {user.email}</p>
      {/* 필요한 다른 사용자 정보 표시 */}
    </div>
  );
};

export default UserInfo;
