import React, { useEffect, useState } from 'react';

const UserInfo: React.FC = () => {
  const [user, setUser] = useState<any>(null); // 사용자 정보 상태
  const [error, setError] = useState<string | null>(null); // 오류 상태
  const [loading, setLoading] = useState<boolean>(true); // 로딩 상태

  useEffect(() => {
    const fetchUserInfo = () => {
      try {
        const userInfo = localStorage.getItem('user'); // 로컬 스토리지에서 사용자 정보 가져오기
        if (userInfo) {
          setUser(JSON.parse(userInfo)); // 사용자 정보 설정
        } else {
          throw new Error('사용자 정보를 찾을 수 없습니다.');
        }
      } catch (err: unknown) {
        // 'unknown' 타입을 'any'로 변환 후 'instanceof' 사용
        if (err instanceof Error) {
          setError(err.message); // 오류 메시지 설정
        } else {
          setError('알 수 없는 오류 발생'); // 알 수 없는 오류 처리
        }
      } finally {
        setLoading(false); // 로딩 완료
      }
    };

    fetchUserInfo();
  }, []);

  if (loading) return <div>로딩 중...</div>;
  if (error) return <div>오류 발생: {error}</div>;
  if (!user) return <div>사용자 정보를 찾을 수 없습니다.</div>;

  return (
    <div>
      <h1>사용자 정보</h1>
      <p>이름: {user.name}</p>
      <p>이메일: {user.email}</p>
      {/* 필요한 다른 사용자 정보 표시 */}
    </div>
  );
};

export default UserInfo;
