import React from 'react';
import { useAuth } from '../hooks/useAuth';
import { loginWithKakao } from '../apis/auth';
import './Login.css'; // Import the CSS file

const Login: React.FC = () => {
  const { setUser } = useAuth();

  const handleKakaoLogin = async () => {
    const token = await getKakaoToken(); // Implement your method to get the token
    const user = await loginWithKakao(token);
    setUser(user);
    console.log("카카오 로그인 버튼 클릭됨");
  };

  const getKakaoToken = async () => {
    // 여기에 카카오 로그인 로직 추가 (SDK 사용)
    return "your_kakao_access_token"; // Mock token for now
  };

  return (
    <div className="login-container">
      <div className="login-background">
        <div className="login-card">
          <div className="login-overlay">
            <div className="login-overlay" style={{ background: 'linear-gradient(169deg, rgba(255, 255, 255, 0) 26%, white 100%)' }} />
            <div className="login-overlay" style={{ background: '#E9DDFF' }} />
            <div className="login-overlay" style={{ background: 'linear-gradient(169deg, white 0%, rgba(255, 255, 255, 0) 72%)' }} />
            <div className="login-overlay" style={{ background: '#FFD9E3' }} />
          </div>
        </div>
      </div>
      <div className="login-title">져니버디와 함께하세요!</div>
      <div className="login-subtitle">친구와 함께하는 스마트한 여행,</div>

      {/* Kakao Login Button */}
      <div className="kakao-login-button" onClick={handleKakaoLogin}>
        <div className="kakao-login-text">카카오 로그인하기</div>
      </div>

      <img style={{ width: 470, height: 370, left: 73, top: 201, position: 'absolute' }} src="../assets/styles/banner/Logo.png" alt="Placeholder" />

      <div className="sign-up-container">
        <div className="sign-up-text">신규 사용자이신가요?</div>
        <div className="sign-up-link">회원가입</div>
      </div>
    </div>
  );
};

export default Login;
